using namespace std;
class Solution
{ 
public: 
int minFlips(vector<vector<int>>&bhavdeep)
{
long long jawab=0; 
if(bhavdeep.size()%2==1&&bhavdeep[0].size()%2==1)
{ 
if(bhavdeep[bhavdeep.size()/2][bhavdeep[0].size()/2]==1)
{ 
jawab=jawab+1; 
} 
} 
vector<long long>nijhawan(3,0); 
if(bhavdeep.size()%2==1)
{ 
long long pehla=0;
long long doosra=bhavdeep[0].size()-1; 
while(pehla<doosra)
{ 
