} 
} 
if(nijhawan[2]%2==1)
{ 
if(nijhawan[1]>0)
{ 
jawab=jawab+nijhawan[1]; 
}
else
{ 
jawab=jawab+2; 
} 
}
else
{ 
jawab=jawab+nijhawan[1]; 
} 
for(long long oo=0;oo<bhavdeep.size()/2;++oo)
{ 
for(long long pp=0;pp<bhavdeep[0].size()/2;++pp)
