class Solution {
    public int minFlips(int[][] grid) {
        int n=grid.length;
        int m=grid[0].length;
        int ans=0;
        System.out.println(n+' '+m);
        if(n%2==1 && m%2==1){
            if(grid[n/2][m/2]==1){
                ans+=1;
                grid[n/2][m/2]=0;
            }
        }
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                int cnt=(grid[i][j]);
                cnt+=(grid[n-1-i][j]);
                cnt+=(grid[i][m-1-j]);
                cnt+=(grid[n-1-i][m-1-j]);
                if(cnt==1 || cnt==3){
                    ans+=1;
                }else if(cnt==2){
                    ans+=2;
                }
            }
        }
        HashMap<Integer,Integer> map=new HashMap<>();
        map.put(0,0);
        map.put(1,0);
        map.put(2,0);
        if(m%2==1){
            for(int i=0;i<n/2;i++){
                int cnt=grid[i][m/2];
                cnt+=grid[n-1-i][m/2];
                map.put(cnt,map.get(cnt)+1);
            }
        }
        System.out.println(ans);
        if(n%2==1){
            for(int i=0;i<m/2;i++){
                int cnt=grid[n/2][i];
                cnt+=grid[n/2][m-1-i];
                map.put(cnt,map.get(cnt)+1);
            }
        }
        System.out.println(ans);
        ans+=map.get(1);
        if(map.get(2)%2==1 && map.get(1)==0){
            ans+=2;
        }
        System.out.println(ans);
        return(ans);

    }
}
