class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        // Check middle horizontal strip
        // Check middle vertical strip

        int e1 = 0, e0 = 0;
        // e1 : matched 1's
        // e0 : unmatched 0's
        int flips = 0;

        if (n % 2) {
            auto &mid = g[n/2];
            for (int j = 0; j < m/2; j++) {
                if (mid[j] == mid[m - 1 - j]) {
                    if (mid[j] == 1) {
                        e1 += 2;
                    }
                } else {
                    // cout << j << " here " << mid[j] << " " << mid[m - 1 - j] << endl;
                    flips++;
                    e0++;
                }
            }
        }
        if (m % 2) {
            int mid = m/2;
            for (int i = 0; i < n/2; i++) {
                if (g[i][mid] == g[n - 1 - i][mid]) {
                    if (g[i][mid] == 1)
                        e1 += 2;
                } else {
                    flips++;
                    e0++;
                }
            }
        }
        if (e1 % 4) {
            if (e0 == 0)
                flips += 2;
        }
        if ((n % 2) && (m % 2)) {
            flips += g[n/2][m/2];
        }
        for (int i = 0; i < n/2; i++) {
            for (int j = 0; j < m/2; j++) {
                int c1 = g[i][j], c2 = g[i][m - 1 - j], c3 = g[n - 1 - i][j], c4 = g[n - 1 - i][m - 1 - j];
                int sum = (c1 + c2 + c3 + c4) % 4;
                if (sum == 3)
                    sum = 1;
                flips += sum;
            }
        }
        return flips;

    }
};
