class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        r=len(grid)
        c=len(grid[0])
        totalFlips=0

        if (r&1) and (c&1):
            if grid[r//2][c//2]==1:
                totalFlips+=1

        freq=[0]*3

        if r&1:
            left,right=0,c-1
            while left<right:
                sumPair=grid[r//2][left]+grid[r//2][right]
                freq[sumPair]+=1
                left+=1
                right-=1

        if c&1:
            top,bottom=0,r-1
            while top<bottom:
                sumPair=grid[top][c//2]+grid[bottom][c//2]
                freq[sumPair]+=1
                top+=1
                bottom-=1

        if freq[2]&1:
            if freq[1]>0:
                totalFlips+=freq[1]
            else:
                totalFlips+=2
        else:
            totalFlips+=freq[1]

        for row in range(r//2):
            for col in range(c//2):
                a=grid[row][col]
                b=grid[r-1-row][col]
                d=grid[row][c-1-col]
                e=grid[r-1-row][c-1-col]

                sumQuad=a+b+d+e

                if sumQuad!=0 and sumQuad!=4:
                    totalFlips+=min(sumQuad,4-sumQuad)

        return totalFlips
