class Solution {
    public int minFlips(int[][] grid) {
        int m=grid.length;
        int n=grid[0].length;

        int flips=0;

        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int c0=0;
                int c1=0;
                if(grid[i][j]==0)c0++;
                else c1++;
                if(grid[i][n-1-j]==0)c0++;
                else c1++;
                if(grid[m-1-i][j]==0)c0++;
                else c1++;
                if(grid[m-1-i][n-1-j]==0)c0++;
                else c1++;
              flips+=Math.min(c0,c1);
            }
        }
        int spflip=0;
        int count1=0;
        if(m%2!=0){
            int i=m/2;
            for(int j=0;j<n/2;j++){
                if(grid[i][j]!=grid[i][n-j-1]){
                    spflip++;
                     flips++;
                }
                else if(grid[i][j]==1) count1+=2;
            }
        }
        if(n%2!=0){
            int j=n/2;
            for(int i=0;i<m/2;i++){
                if(grid[i][j]!=grid[m-1-i][j]){
                    spflip++;
                    flips++;
                }
                 else if(grid[i][j]==1) count1+=2;
            }
        }
        if(n%2!=0&&m%2!=0&&grid[m/2][n/2]==1){
               flips++;
        }
        if(spflip==0&&count1%4!=0) flips+=2;
        return flips;
    }
}
