class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans2=0;
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        unordered_map<int,int>mp;
        if (n%2==1&&m%2==1) {
            if (grid[n/2][m/2] == 1)
                ans += 1;
        }
        vector<int>st(3, 0);
        if (n % 2 == 1) {
            int a = 0, b = m - 1;
            while (a < b) {
                int sm = grid[n/2][b]+grid[n/2][a];
                st[sm] += 1;
                mp[sm]++;
                a += 1;
                b -= 1;
            }
        }
         if(mp.find(m/2)!=mp.end()){
             ans2++;
         }

        if (m % 2 == 1) {
            int x = 0, y = n - 1;
            while ( x< y) {
                int sm = grid[x][m / 2] + grid[y][m / 2];
                st[sm] += 1;
                mp[sm]++;

                x += 1;
                y -= 1;
            }
        }

        // if (m % 2 == 1) {
        //     int a = 0, b = n - 1;
        //     while (a < b) {
        //         int sm = grid[a][m / 2] + grid[b][m / 2];
        //         st[sm] += 1;
        //         mp[sm]++;
        //         a += 1;
        //         b -= 1;
        //     }
        // }
        if (st[2] % 2 == 1) {
            if (st[1] > 0) {
                ans +=st[1];
            } else {
                ans += 2;
            }
            mp[ans]++;
        } else {
            ans += st[1];
        }
         ans2=min(ans2,ans);
        for (int i = 0;i<n/2; ++i) {
            for (int j = 0; j <m/2; ++j) {
               int z2= grid[n - 1 - i][j];
                int z1= grid[i][j];
                int z4= grid[n - 1 - i][m - 1 - j];
                int z3= grid[i][m - 1 - j];
                int zz = z1 + z2 +z3+z4;
                if (zz!=0&&zz!= 4) {
                    ans += min(zz,4-zz);
                }
            }
        }

        return ans;
    }
};
