class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int totalFlips = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                totalFlips += 1;
            }
        }

        vector<int> shifts(3, 0);
        if (n % 2 == 1) {
            int left = 0, right = m - 1;
            while (left < right) {
                int sum = grid[n / 2][left] + grid[n / 2][right];
                shifts[sum] += 1;
                left++;
                right--;;
            }
        }
        if (m % 2 == 1) {
            int top = 0, bottom = n - 1;
            while (top < bottom) {
                int sum = grid[top][m / 2] + grid[bottom][m / 2];
                shifts[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        if (shifts[2] % 2 == 1) {
            if (shifts[1] > 0) totalFlips += shifts[1];
            else totalFlips += 2;

        } 
        else  totalFlips += shifts[1];

        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                int a = grid[i][j];
                int b = grid[n - 1 - i][j];
                int c = grid[i][m - 1 - j];
                int d = grid[n - 1 - i][m - 1 - j];
                int sum = a + b + c + d;
                if (sum != 0 && sum != 4) {
                    totalFlips += min(sum, 4 - sum);
                }
            }
        }
        return totalFlips;
    }
};
