class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        vector<vector<int>> temp = grid;
        int m=grid.size();
        int n=grid[0].size();
        int flip = 0, mid = 0;

        for(int i=0; i<m; i++){
            int x=0, y=n-1;
            while(x<y){
                if(temp[i][x] != temp[i][y]){
                    if(temp[m-1-i][x] == temp[m-1-i][y]){
                        cout << "y";
                        temp[i][x] = temp[m-1-i][x];
                        temp[i][y] = temp[m-1-i][x];
                    } else {
                        cout << "x";
                        temp[i][x] = 0;
                        temp[i][y] = 0;
                    }
                    flip++;
                    if((double)(m/2) != m/2.0){
                        if(i == m/2){
                            mid++;
                        }
                    }
                }
                x++;
                y--;
            }
        }

        cout << flip << " ";

        int col = 0;

        for(int i=0; i<n; i++){
            int x=0, y=m-1;
            while(x<y){
                if(temp[x][i] != temp[y][i]){
                    if(temp[x][n-1-i] == temp[y][n-1-i]){
                        temp[x][i] = temp[y][i] = temp[x][n-1-i];
                    } else {
                        temp[x][i] = temp[y][i] = 0;
                    }
                    flip++;
                    if((double)(n/2) != n/2.0){
                        if(i == n/2){
                            mid++;
                        }
                    }
                }
                x++;
                y--;
            }
        }

        cout << flip << " ";

        int ones = 0;

        if((double)(m/2) != m/2.0){
            int r = m/2;
            for(int i=0; i<n; i++){
                if(temp[r][i] == 1){
                    cout << r << i << " ";
                    ones++;
                }
            }
        }

        if((double)(n/2) != n/2.0){
            int c = n/2;
            for(int i=0; i<m; i++){
                if(temp[i][c] == 1){
                    cout << i << c << " ";
                    ones++;
                }
            }
        }

        if(((double)(n/2) != n/2.0) && ((double)(m/2) != m/2.0)){
            if(temp[m/2][n/2] == 1){
                ones--;
            }
        }

        cout << "ones = " << ones << " ";

        if(ones%2 == 1){
            ones--;
            flip++;
        }

        cout << flip << " " << "mid = " << mid << " ones = " << ones << " ";

        ones = ones % 4;
        ones /= 2;
        ones -= min(ones, mid);
        ones *= 2;

        flip += ones;


        cout << flip << endl;

        return flip;
    }
};
