class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int c = g[i][j] + g[n-1-i][j] + g[i][m-j-1] + g[n-i-1][m-j-1];
                ans += min(c, 4 - c);
            }
        }
        int cur = 0;
        int t = 0;
        if (n % 2) {
            for (int j = 0; j < m / 2; ++j) {
                cur += (g[n / 2][j] != g[n/2][m-j-1]);
            }
            for (int j = 0; j < m; ++j) {
                if (g[n / 2][j] == g[n/2][m-j-1])
                    t += g[n / 2][j];
            }
        }
        if (m % 2) {
            for (int j = 0; j < n / 2; ++j) {
                cur += (g[j][m / 2] != g[n-j-1][m/2]);
            }

            for (int j = 0; j < n; ++j) {
                if (g[j][m / 2] == g[n-j-1][m/2])
                    t += g[j][m / 2];
            }
        }
        if (n % 2 && m % 2 && g[n/2][m/2]) {
            ++ans;
            t -= 2;
        }

        if ((t + cur) % 4 == 2) {
            if (cur == 0) {
                cur += 2;
            }
        }
        return ans + cur;
    }
};
