import java.util.*;

class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;
        int totalFlips = 0;

        // Check the center cell if both dimensions are odd
        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips += 1;
            }
        }

        int[] flipCount = new int[3];

        // Count flips needed for rows if the number of rows is odd
        if (rows % 2 == 1) {
            int leftIndex = 0, rightIndex = cols - 1;
            while (leftIndex < rightIndex) {
                int sum = grid[rows / 2][leftIndex] + grid[rows / 2][rightIndex];
                flipCount[sum] += 1;
                leftIndex += 1;
                rightIndex -= 1;
            }
        }

        // Count flips needed for columns if the number of columns is odd
        if (cols % 2 == 1) {
            int topIndex = 0, bottomIndex = rows - 1;
            while (topIndex < bottomIndex) {
                int sum = grid[topIndex][cols / 2] + grid[bottomIndex][cols / 2];
                flipCount[sum] += 1;
                topIndex += 1;
                bottomIndex -= 1;
            }
        }

        // Update the totalFlips based on flipCount
        if (flipCount[2] % 2 == 1) {
            if (flipCount[1] > 0) {
                totalFlips += flipCount[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipCount[1];
        }

        // Handle the corners of each 2x2 block
        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int sum = topLeft + bottomLeft + topRight + bottomRight;

                if (sum != 0 && sum != 4) {
                    totalFlips += Math.min(sum, 4 - sum);
                }
            }
        }

        return totalFlips;
    }
}
