class Solution {
public:

    int f(vector<vector<int>>&v,int a,int b,int c,int d){
        int n=v.size(),m=v[0].size();
        if(a>=c||b>=d){
            return 0;
        }
        int one=v[a][b]+v[a][d]+v[c][b]+v[c][d];
        int res=min(one,4-one);
        // cout<<res<<" "<<a<<" "<<b<<" "<<c<<" "<<d<< endl;
        res+=f(v,a+1,b,c-1,d);
        return res;
    }

    int minFlips(vector<vector<int>>& v) {
        int n=v.size(),m=v[0].size();
        // if(n==1&&m==1) return (v[0][0]==1);
        int res=0;
        int i=0,j=m-1;
        while(i<j){
            res+=f(v,0,i,n-1,j);
            i++,j--;
        }
        // if(n==1||m==1) goto l1;
        cout<<res<<endl;
        if(n%2==0&&m%2==0){
            return res;
        }
        if(m%2==0){
            int cnt=0,one=0;
            int j=0,k=m-1;
            while(j<k){
                if(v[n/2][j]!=v[n/2][k]){
                    cnt++;
                }
                else if(v[n/2][j]){
                    one+=2;
                }
                j++,k--;
            }
            if(one%4==0){
                return res+cnt;
            }
            else{
                if(cnt){
                    return res+cnt;
                }
                else{
                    return res+cnt+2;
                }
            }
        }
        if(n%2==0){
            // cout<<res<<endl;
            int cnt=0,one=0;
            int i=0,k=n-1;
            while(i<k){
                if(v[i][m/2]!=v[k][m/2]){
                    cnt++;
                }
                else if(v[i][m/2]){
                    one+=2;
                }
                i++,k--;
            }
            if(one%4==0){
                return res+cnt;
            }
            else{
                if(cnt){
                    return res+cnt;
                }
                else{
                    return res+cnt+2;
                }
            }
        }
        if(v[n/2][m/2])res++;
        // cout<<v[n/2+1][m/2+1]<<" "<<n/2+1<<" "<<m/2+1<<" "<<n/2<<" "<<m/2<< endl;
        // l1:
        int cnt=0,one=0;
        i=0,j=0;
        int k=n-1;
        while(i<k){
            if(v[i][m/2]!=v[k][m/2]){
                cnt++;
            }
            else if(v[i][m/2]){
                one+=2;
            }
            i++,k--;
        }
        j=0,k=m-1;
        while(j<k){
            if(v[n/2][j]!=v[n/2][k]){
                cnt++;
            }
            else if(v[n/2][j]){
                one+=2;
            }
            j++,k--;
        }
        if(one%4==0||cnt){
            return res+cnt;
        }
        return res+cnt+2;
    }
};
