class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int ct=0;
        int ans=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int t=0;
                t+=grid[i][j];
                t+=grid[n-i-1][j];
                t+=grid[i][m-j-1];
                t+=grid[n-i-1][m-j-1];
                ct+=min(t,4-t);
            }
        }
        int opr=0,opc=0;
        int onec=0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]!=grid[n/2][m-j-1])
                opr++;
                else if(grid[n/2][j]==1){
                    onec+=2;
                }
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-i-1][m/2])
                opc++;
                else if(grid[i][m/2]==1)
                onec+=2;
            }
        }
        // cout<<ct<<" "<<opr<<" "<<opc<<" "<<onec<<endl;
        if(n%2&&m%2&&grid[n/2][m/2]==1){
            ct++;
        }
        if(onec%4==2&&opr==0&&opc==0)
        ct+=2;
        return opr+opc+ct;
    }
};
