class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int CalculatingALLTotalSides = 0;
        int NoOfRowCount = grid.size();
        int NoOfColCount = grid[0].size();
        vector<int> cnt(3, 0);

        if (NoOfRowCount % 2 == 1 && NoOfColCount % 2 == 1) {
            if (grid[NoOfRowCount / 2][NoOfColCount / 2] == 1) {
                ans += 1;
            }
        }

        if (NoOfRowCount % 2 == 1) {
            int l = 0, r = NoOfColCount - 1;
            while (l < r) {
                int BothAdding = grid[NoOfRowCount / 2][l] + grid[NoOfRowCount / 2][r];
                cnt[BothAdding] += 1;
                ++l;
                --r;
            }
        }

        if (NoOfColCount % 2 == 1) {
            int top = 0, down = NoOfRowCount - 1;
            while (top < down) {
                int BothAdding = grid[top][NoOfColCount / 2] + grid[down][NoOfColCount / 2];
                cnt[BothAdding] += 1;
                ++top;
                --down;
            }
        }

        if (cnt[2] % 2 == 1) {
            if (cnt[1] > 0) {
                ans += cnt[1];
            } else {
                ans += 2;
            }
        } else {
            ans += cnt[1];
        }

        for (int i = 0; i < NoOfRowCount / 2; ++i) {
            for (int j = 0; j < NoOfColCount / 2; ++j) {
                int UpSideStarting = grid[i][j];
                int downSideStarting = grid[NoOfRowCount - 1 - i][j];
                int UpsideEnding = grid[i][NoOfColCount - 1 - j];
                int downSideEnding = grid[NoOfRowCount - 1 - i][NoOfColCount - 1 - j];

                CalculatingALLTotalSides = UpSideStarting + downSideStarting + UpsideEnding + downSideEnding;

                if (CalculatingALLTotalSides != 0 && CalculatingALLTotalSides != 4) {
                    ans += min(CalculatingALLTotalSides, 4 - CalculatingALLTotalSides);
                }
            }
        }

        return ans;
    }
};
