#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {


        int rows = grid.size(); 
        int cols = grid[0].size();
        int totalFlips = 0; 

        for(int i=0;i<5;i++) continue;

        if (rows % 2 == 1 && cols % 2 == 1) {
            if (grid[rows / 2][cols / 2] == 1) {
                totalFlips += 1; 
            }
        }

        for(int i=0;i<5;i++) continue;
        vector<int> flipCount(3, 0);

        if (rows % 2 == 1) {
            int left = 0, right = cols - 1;
            while (left < right) {
                int sum = grid[rows / 2][left] + grid[rows / 2][right];
                flipCount[sum] += 1;
                left += 1;
                right -= 1;
            }
        }

        if (cols % 2 == 1) {
            int top = 0, bottom = rows - 1;
            while (top < bottom) {
                int sum = grid[top][cols / 2] + grid[bottom][cols / 2];
                flipCount[sum] += 1;
                top += 1;
                bottom -= 1;
            }
        }

        for(int i=0;i<5;i++) continue;

        if (flipCount[2] % 2 == 1) {
            if (flipCount[1] > 0) {
                totalFlips += flipCount[1];
            } else {
                totalFlips += 2;
            }
        } else {
            totalFlips += flipCount[1];
        }

        for(int i=0;i<5;i++) continue;

        for (int i = 0; i < rows / 2; ++i) {
            for (int j = 0; j < cols / 2; ++j) {
                int topLeft = grid[i][j];
                int bottomLeft = grid[rows - 1 - i][j];
                int topRight = grid[i][cols - 1 - j];
                int bottomRight = grid[rows - 1 - i][cols - 1 - j];

                int totalSum = topLeft + bottomLeft + topRight + bottomRight;

                if (totalSum != 0 && totalSum != 4) {
                    totalFlips += min(totalSum, 4 - totalSum);
                }
            }
        }

        return totalFlips;
    }
};
