class Solution {
private:  
    int f(int ind,vector<int>&temp,vector<int>& dp){
        if( ind == temp.size() ) return 0;
        if( dp[ind] != -1 ) return dp[ind];
        int zero = temp[ind] +f(ind+1,temp,dp);
        int ones= (4-temp[ind]) + f(ind+1,temp,dp) ;

        return dp[ind]=min(zero,ones);
    }
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size(),i=0,j=n-1;
        // cout<<i<<" "<<j<<" "<<n<<" "<< m<<endl;
        vector<int> temp,r,c;
        while( i <= j ){
            if( i == j ){
                for(int col=0;col<(ceil(m/2));col++){
                    r.push_back(grid[i][col]+grid[i][m-col-1]);
                }
            }
            else{
                int cnt=0;
                for(int col=0;col<(m/2);col++){
                    cnt=(grid[i][col]+grid[j][col]+grid[i][m-col-1]+grid[j][m-col-1]);
                    temp.push_back(cnt);
                }  
                if( m & 1 ){
                    c.push_back(grid[i][m/2]+grid[j][m/2]);
                } 
            }
            i++;
            j--;
        }
        // for(auto  it: temp)cout<<it<<" ";
        // cout<<endl;
        int sz = temp.size();
        vector<int> dp(sz+1,-1);
        int part = f(0,temp,dp);
        // cout<<part<<endl;
        if( (n & 1 )|| (m & 1) ){
            // c ....
            int c1=0,c2=0;
            if( m  & 1 ){
                for(int row=0;row<(n/2);row++){
                    if( grid[row][(m/2)] == grid[n-row-1][m/2] ){
                        if( grid[row][m/2] )c1++;
                    }
                    else{
                        c2++;
                    }
                }
            }
            if( n & 1 ){
                for(int col=0;col<(m/2);col++){
                    if( grid[n/2][col] == grid[n/2][m-col-1] ){
                        if( grid[n/2][col] )c1++;
                    }
                    else{
                        c2++;
                    }
                }
            }
            // cout<<c1<<"  "<<c2<<endl;
            if( (n&1) && (m&1) && grid[n/2][m/2] == 1 ) part++;
            part+=(c2);
            if( c1 %2 != 0 ){
                if( c2 == 0 ){
                    part+=(2);
                }   
            }
        }
        return part;
    }
};
