class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int cnt1 = 0, cnt2 = 0;
        int tot = 0;
        int ans = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt =  0;
                cnt+=grid[i][j]+grid[n-1-i][j]+grid[i][m-1-j]+grid[n-1-i][m-1-j];
                if(cnt%2==1) ans++;
                else if(cnt%4==2) ans+=2;
            }
        }
        if(n%2==1 and m%2==1 and grid[n/2][m/2]==1) ans++;
        int c1= 0, c2=0;
        if(n%2==1){
            for(int i=0;i<m;i++){
                if(grid[n/2][i]==1) c1++;
                if(grid[n/2][i]!=grid[n/2][m-1-i] and i<m/2) cnt1++;
            }
        }
        if(m%2==1){
            for(int i=0;i<n;i++){
                if(grid[i][m/2]==1) c2++;
                if(grid[i][m/2]!=grid[n-1-i][m/2] and i<n/2) cnt2++;
            }
        }
        if(n%2==1 and m%2==1 and grid[n/2][m/2]==1) {
            c1--;
            c2--;
        }
        cout<<ans<<" "<<cnt1<<" "<<cnt2<<endl;
        ans+=cnt1;
        ans+=cnt2;
        cout<<c1<<" "<<c2<<endl;
        if((c1+c2)%4 == 2 and cnt1==0 and cnt2==0) return ans+2;
        return ans;
    }
};
