class Solution:
    def handle_middle_element(self, grid, rows, cols):
        return 1 if rows % 2 == 1 and cols % 2 == 1 and grid[rows // 2][cols // 2] == 1 else 0

    def process_mid_row(self, grid, mid_row, adjustments):
        left, right = 0, len(grid[0]) - 1
        while left < right:
            sum_val = grid[mid_row][left] + grid[mid_row][right]
            adjustments[sum_val] += 1
            left += 1
            right -= 1

    def process_mid_col(self, grid, mid_col, adjustments):
        top, bottom = 0, len(grid) - 1
        while top < bottom:
            sum_val = grid[top][mid_col] + grid[bottom][mid_col]
            adjustments[sum_val] += 1
            top += 1
            bottom -= 1

    def handle_shift_adjustments(self, adjustments):
        if adjustments[2] % 2 == 1:
            return adjustments[1] if adjustments[1] > 0 else 2
        else:
            return adjustments[1]

    def process_corners(self, grid, rows, cols):
        flips = 0
        for i in range(rows // 2):
            for j in range(cols // 2):
                a = grid[i][j]
                b = grid[rows - 1 - i][j]
                c = grid[i][cols - 1 - j]
                d = grid[rows - 1 - i][cols - 1 - j]

                sum_val = a + b + c + d

                if sum_val != 0 and sum_val != 4:
                    flips += min(sum_val, 4 - sum_val)
        return flips

    def minFlips(self, grid):
        rows, cols = len(grid), len(grid[0])
        total_flips = 0

        total_flips += self.handle_middle_element(grid, rows, cols)

        adjustments = [0] * 3

        if rows % 2 == 1:
            self.process_mid_row(grid, rows // 2, adjustments)

        if cols % 2 == 1:
            self.process_mid_col(grid, cols // 2, adjustments)

        total_flips += self.handle_shift_adjustments(adjustments)
        total_flips += self.process_corners(grid, rows, cols)

        return total_flips
