class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m=grid.size(), n=grid[0].size();
        if(m==1 && n==1){
            if(grid[0][0]==1)   return 1;
            else                return 0;
        }
        int ans=0;
        for(int i=0; i<m/2; i++){
            for(int j=0; j<n/2; j++){
                // count no of 1 on all corners
                int c1=0;
                if(grid[i][j]==1)   c1++;
                if(grid[m-i-1][j]==1)   c1++;
                if(grid[i][n-j-1]==1)   c1++;
                if(grid[m-i-1][n-j-1]==1)   c1++;
                ans+=min(c1, 4-c1);
            }
        }
        cout<<ans<<endl;
        int rowans=0;
        int notpali=0, perfect1=0;
        if(m%2==1){
            int i=m/2;
            for(int j=0; j<n/2; j++){
                if(grid[i][j]==grid[i][n-j-1]){
                    if(grid[i][j]==1)   perfect1++;
                }
                else    notpali++;
            }
        }
        if(n%2==1){
            int j=n/2;
            for(int i=0; i<m/2; i++){
                if(grid[i][j]==grid[m-i-1][j]){
                    if(grid[i][j]==1)   perfect1++;
                }
                else    notpali++;
            }
        }
        // cout<<notpali<<" "<<perfect1<<endl;
        if(perfect1%2==0)   ans+=notpali;
        else{
            if(notpali>0)   ans+=notpali;
            else            ans+=2;
        }
        if(m&1 && n&1 && grid[m/2][n/2]==1) ans++;
        return ans;
    }
};
