class Solution {
public:
  int minFlips(vector<vector<int>>& grid) {
    int m,n,ans,i,j;
        n = grid.size();
         m = grid[0].size();
        ans = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans += 1;
            }
        }

        vector<int> v(3, 0);
        if (n % 2 == 1) {
            int low = 0;
            int high = m - 1;
            while (low < high) {
                int sum = grid[n / 2][low] + grid[n / 2][high];
                v[sum] += 1;
                low++;high--;
            }
        }

        if (m % 2 == 1) {
             int low = 0;
            int high = n- 1;
            while (low < high) {
                int sum= grid[low][m / 2] + grid[high][m / 2];
                 v[sum] += 1;
                low++;high--;
            }
        }

        if (v[2] % 2 == 1) {
            if (v[1] > 0) {
                ans += v[1];
            } else {
                ans += 2;
            }
        } else {
            ans += v[1];
        }

        for (i = 0; i < n / 2; ++i) {
            for (j = 0; j < m / 2; ++j) {
int sum = grid[i][j]+grid[n - 1 - i][j]+grid[i][m - 1 - j]+grid[n - 1 - i][m - 1 - j];
                if (sum != 0 && sum != 4) {
                    ans += min(sum, 4 - sum);
                }
            }
        }

        return ans;
    }
};
