class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        // for even
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                vector<int> v(4);
                v[0] = grid[i][j];
                v[1] = grid[i][m - j - 1];
                v[2] = grid[n - i - 1][j];
                v[3] = grid[n - i - 1][m - j - 1];
                int one = 0, zero = 0;
                for (auto it : v) {
                    if (it == 1) {
                        one++;
                    } else {
                        zero++;
                    }
                }
                ans += min(one, zero);
            }
        }
        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                ans++;
            }
        }
        int one_pair = 0;
        int uneq = 0;
        if (n % 2 == 1) {
            for (int j = 0; j < m / 2; j++) {
                if (grid[n / 2][j] == grid[n / 2][m - j - 1]) {
                    if (grid[n / 2][j] == 1) {
                        one_pair++;
                    }
                } else {
                    uneq++;
                }
            }
        }
        if (m % 2 == 1) {

            for (int i = 0; i < n / 2; i++) {
                if (grid[i][m / 2] == grid[n - i - 1][m / 2]) {
                    if (grid[i][m / 2] == 1) {
                        one_pair++;
                    }
                } else {
                    uneq++;
                }
            }
        }
        if (one_pair % 2 == 0) {
            ans += uneq;
        } else {
            if (uneq > 0) {
                ans += uneq;
            } else {
                ans += 2;
            }
        }
        return ans;
    }
};
