class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(),m = grid[0].size();
        int res = 0;
        for(int i = 0;i<n/2;++i){
            for(int j = 0;j<m/2;++j){
                int cnt = grid[i][j] + grid[n-i-1][j];
                cnt += grid[i][m-j-1] + grid[n-i-1][m-j-1];
                res += min(cnt,(4-cnt)%4);
            }
        }
        if(n % 2 == 0 && m % 2 == 0)return res;    
        if(n % 2 == 1 && m % 2 == 0){
            bool flag = true;
            int sum = 0;
            for(int i = 0,j = m-1;i<j;++i,--j){
                sum += grid[n/2][i] + grid[n/2][j];
                if(grid[n/2][i] != grid[n/2][j]){
                    res += 1;
                    flag = false;
                }
            }
            if(flag && sum % 4 == 2)res += 2;
            return res;
        }
        if(n % 2 ==0 && m % 2 == 1){
            bool flag = true;
            int sum = 0;
            for(int i = 0,j = n-1;i<j;++i,--j){
                sum += grid[i][m/2] + grid[j][m/2];
                if(grid[i][m/2] != grid[j][m/2]){
                    res += 1;
                    flag = false;
                }
            }
            if(flag && sum % 4 == 2)res += 2;
            return res;
        }
        int sum = 0;
        bool flag = true;
        for(int i = 0,j = m-1;i<j;++i,--j){
            sum += grid[n/2][i] + grid[n/2][j];
            if(grid[n/2][i] != grid[n/2][j]){
                res += 1;
                flag = false;
            }
        }
        for(int i = 0,j = n-1;i<j;++i,--j){
            sum += grid[i][m/2] + grid[j][m/2];
            if(grid[i][m/2] != grid[j][m/2]){
                res += 1;
                flag = false;
            }
        }
        if(sum % 4 == 2 && flag)res += 2;
        return res + grid[n/2][m/2];

    }
};
