class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        vector<int>v(3, 0);
        if(n % 2 == 1 && m % 2 == 1) 
        {
            if (grid[n/2][m/2] == 1) 
            {
                ans += 1;
            }
        }
        if(n % 2 == 1) 
        {
            int a = 0, b = m - 1;
            while (a < b) 
            {
                int sm = grid[n / 2][a] + grid[n / 2][b];
                v[sm] += 1;
                a += 1;
                b -= 1;
            }
        }
        if(m % 2 == 1) 
        {
            int a = 0, b = n - 1;
            while (a < b) {
                int sm = grid[a][m / 2] + grid[b][m / 2];
                v[sm] += 1;
                a += 1;
                b -= 1;
            }
        }
        if (v[2] % 2 == 1) 
        {
            if (v[1] > 0) 
            {
                ans += v[1];
            } 
            else
            {
                ans += 2;
            }
        } else {
            ans += v[1];
        }
        for(int i = 0; i < n / 2; i++) 
        {
            for(int j = 0; j < m / 2; j++) 
            {
                int a = grid[i][j];
                int b = grid[n - i - 1][j];
                int c = grid[i][m - j - 1];
                int d = grid[n - i - 1][m - j - 1];
                int sum = a + b + c + d;
                if(sum != 0 && sum != 4) 
                {
                    ans += min(sum, 4 - sum);
                }
            }
        }
        return ans;
    }
};
