class Solution {
    int solve(vector<vector<int>>& grid){
   int n = grid.size(), m = grid[0].size();
        int res = 0;

        if (n % 2 == 1 && m % 2 == 1) {
            if (grid[n / 2][m / 2] == 1) {
                res += 1;
            }
        }

        int v[3]={0};
        if (n & 1) {
            int x = 0, y = m - 1;
            while (x < y) {
                int pos = grid[n / 2][x] + grid[n / 2][y];
                v[pos] += 1;
                x += 1;
                y -= 1;
            }
        }

        if (m & 1) {
            int a = 0, b = n - 1;
            while (a < b) {
                int pos = grid[a][m / 2] + grid[b][m / 2];
                v[pos] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (v[2] & 1) {
            if (v[1] > 0) {
                res += v[1];
            } else {
                res += 2;
            }
        } else {
            res += v[1];
        }

        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int x = grid[i][j];
                int y = grid[n - 1 - i][j];
                int z = grid[i][m - 1 - j];
                int w = grid[n - 1 - i][m - 1 - j];

                int pos = x + y + z + w;

                if (pos != 0 && pos != 4) {
                    res += min(pos, 4 - pos);
                }
            }
        }

        return res;
    }
public:
    int minFlips(vector<vector<int>>& grid) {
        return solve(grid);
    }
};
