class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        if(n == 1 && m == 1)
            return grid[0][0];
        else if(n == 1 )
        {
            int c = 0, cnt=0;
            for(int j=0;j<m/2;j++)
            {
                if(grid[0][j] != grid[0][m-j-1])
                    c++;
                else if(grid[0][j] == 1)
                    cnt+=2;
            }

            if(c == 0)
                c = cnt%4;

            if(m%2 == 1)
                c += grid[0][m/2];

            return c;
        }
        else if(m == 1)
        {
            int c = 0, cnt=0;
            for(int i=0;i<n/2;i++)
            {
                if(grid[i][0] != grid[n-i-1][0])
                    c++;
                else if(grid[i][0] == 1)
                    cnt+=2;
            }

            if(c == 0)
                c = cnt%4;

            if(n%2 == 1)
                c += grid[n/2][0];

            return c;
        }
        else
        {
            int ans = 0;
            for(int i=0;i<n/2;i++)
            {
                for(int j=0;j<m/2;j++)
                {
                    int count = 0;
                    int val = grid[i][j];
                    if(val != grid[i][m-j-1])
                        count++;
                    if(val != grid[n-i-1][m-j-1])
                        count++;
                    if(val != grid[n-i-1][j])
                        count++;

                    ans += min(count, 4-count);
                }
            }

            int cnt = 0, c=0;
            if(n%2 == 1)
            {
                for(int j=0;j<m/2;j++)
                {
                    if(grid[n/2][j] != grid[n/2][m-j-1])
                        c++;
                    else if(grid[n/2][j] == 1)
                        cnt+=2;
                }
            }
            if(m%2 == 1)
            {
                for(int i=0;i<n/2;i++)
                {
                    if(grid[i][m/2] != grid[n-i-1][m/2])
                        c++;
                    else if(grid[i][m/2] == 1)
                        cnt+=2;
                }
            }

            if(n%2 == 1 && m%2 == 1 && grid[n/2][m/2] == 1)
                ans++;

            if(c == 0)
                c = (cnt%4);
            ans += c;
            return ans;
        }
    }
};
