class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
         int m=grid.size(),n=grid[0].size(),ans=0;
         vector<vector<int>>v0(m,vector<int>(n,0)),v1(m,vector<int>(n,0));
        for(int i=0;i<((m)/2);i++)
        {
            for(int j=0;j<((n)/2);j++)
            {
                int ones=0;
                ones=(grid[i][j]+grid[i][n-j-1]+grid[m-i-1][j]+grid[m-i-1][n-j-1]);
                //cout<<grid[i][j]<<grid[i][n-j-1]<<grid[m-i-1][j]<<grid[m-i-1][n-j-1]<<"\n";
                //cout<<i<<j<<n-j-1<<m-i-1
                if(ones>2) ans+=(4-ones);
                else ans+=ones;
                cout<<ones<<" "<<ans<<"\n";
            }
        }
        cout<<ans<<"\n";
        int tones=0,f=0,c=0;
        if(m%2!=0)
        {
            int x=(m/2);
           for(int j=0;j<(n/2);j++) 
           {
              if(grid[x][j]!=grid[x][n-j-1])
              {
                ans++;
                tones+=2;
                f=1;
              }
            else if(grid[x][j]==1) tones+=2;
           }
        }
        cout<<ans<<"\n";
        if(n%2!=0)
        {
            int y=(n/2);
           for(int i=0;i<(m/2);i++) 
           {
              if(grid[i][y]!=grid[m-i-1][y])
              {
                ans++;
                tones+=2;
                f=1;
              }
              else if(grid[i][y]==1) tones+=2;
           }
        }
        cout<<ans<<"\n";
        if(m%2!=0 && n%2!=0)
        {
            int x=m/2,y=n/2;
            ans+=grid[x][y];
        }
        cout<<ans<<"\n";
        if(tones%4==2)
        {
            if(f==0) ans+=2;
        }
        return ans;
    }
};
