class Solution {
public:
  int minFlips(vector<vector<int>>&  g) {
    int a = 0, m = g.size(), n = g[0].size();
    int i =0,l = m-1;
    int j = 0, k = n-1;
    for(; i<l; ++i, --l) {
      for(j = 0, k = n-1; j<k; ++j, --k) {
        int cur = g[i][j]+g[i][k]+g[l][j]+g[l][k];
        a+=min(cur, 4-cur);
      }
    }
    int o = 0, c = 0;
    i = m/2, j = n/2;
    if((m%2) && (n%2) && g[i][j]) ++a;
    if(n%2) for(int x = 0, y = m-1; x<y; ++x, --y) {
      if(g[x][j]!=g[y][j]) {
        ++a, ++o, ++c;
      }
      else if(g[x][j]) ++o;
    }
    if(m%2) for(int x = 0, y = n-1; x<y; ++x, --y) {
      if(g[i][x]!=g[i][y]) {
        ++a, ++o, ++c;
      }
      else if(g[i][x]) ++o;
    }
    if(c==0 &&(o%2==1)) a+=2;
    return a;
  }
};
