class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
      int n=grid.size(),m=grid[0].size();
        int res=0,res2=0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int cnt=grid[i][m-j-1]+grid[i][j]+grid[n-1-i][j]+grid[n-i-1][m-1-j];
                if(cnt>2) res+=(4-cnt);
                else res+=cnt;
            }
        }
        //cout<<res<<" res value"<<endl;
        if(n%2 && m%2){
            int cnt=0,pairs=0;
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==grid[n/2][m-1-i] && grid[n/2][i]) pairs++;
                else if(grid[n/2][i]||grid[n/2][m-1-i])cnt++;
            }
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==grid[n-i-1][m/2] && grid[i][m/2]) pairs++;
                else if(grid[i][m/2]||grid[n-i-1][m/2])cnt++;
            }
            //cout<<"cnt value "<<cnt<<" pairs value "<<pairs<<endl;
            if(cnt) res+=cnt;
            else if(pairs%2) res+=2;
            if(grid[n/2][m/2])res++;
        }else if(n%2){
            int cnt=0,pairs=0;
            for(int i=0;i<m/2;i++){
                if(grid[n/2][i]==grid[n/2][m-1-i] && grid[n/2][i]) pairs++;
                else if(grid[n/2][i]||grid[n/2][m-1-i])cnt++;
            }
            //cout<<"cnt value "<<cnt<<" pairs value "<<pairs<<endl;
            if(cnt) res+=cnt;
            else if(pairs%2) res+=2;
        }else if(m%2){
            int cnt=0,pairs=0;
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==grid[n-i-1][m/2] && grid[i][m/2]) pairs++;
                else if(grid[i][m/2]||grid[n-i-1][m/2]) cnt++;
            }
            //cout<<"cnt value "<<cnt<<" pairs value "<<pairs<<endl;
            if(cnt) res+=cnt;
            else if(pairs%2) res+=2;
        }
        return res;
    }
};
