class Solution {
    public int minFlips(int[][] grid) {
        int row = grid.length;
        int col = grid[0].length;
        int res = 0;

        for(int i = 0; i < (grid.length)/2; i++){
            for(int j = 0; j < (grid[i].length)/2; j++){
                int lt = grid[i][j];
                int rt = grid[i][col - j - 1];
                int ld = grid[row - i - 1][j];
                int rd = grid[row - i - 1][col - j - 1];
                int cnt0 = 0;
                int cnt1 = 0;
                if(lt == 0) cnt0++;
                else cnt1++;
                if(rt == 0) cnt0++;
                else cnt1++;
                if(ld == 0) cnt0++;
                else cnt1++;
                if(rd == 0) cnt0++;
                else cnt1++;
                res += Math.min(cnt0,cnt1);
            }
        }
        if(row % 2 == 1 && col % 2 == 0){
            int t = 0;
            int cnt1 = 0;
            for(int i = 0; i < col/2; i++){
                if(grid[row/2][i] != grid[row/2][col - i - 1]) t++;
            }
            for(int i = 0; i < col; i++){
                if(grid[row/2][i] == 1) cnt1++;
            }
            int mi = cnt1/4*4;
            int ma = (cnt1/4+1)*4;
            if((cnt1 + t) >= ma || (cnt1 - t) <= mi)
                return res + t;
            return res + t + 2;
        }
        if(col % 2 == 1 && row % 2 == 0){
            int t = 0;
            int cnt1 = 0;
            for(int i = 0; i < row/2; i++){
                if(grid[i][col/2] != grid[row - i - 1][col/2]) t++;
            }
            for(int i = 0; i < row; i++){
                if(grid[i][col/2] == 1) cnt1++;
            }
            //System.out.println(res+" "+t+" "+cnt1);
            int mi = cnt1/4*4;
            int ma = (cnt1/4+1)*4;
            if((cnt1 + t) >= ma || (cnt1 - t) <= mi)
                return res + t;
            return res + t + 2;
        }
        if(row % 2 == 1 && col % 2 == 1){
            if(grid[row/2][col/2] == 1){
                grid[row/2][col/2] = 0;
                res++;
            }
            int t = 0;
            int cnt1 = 0;
            for(int i = 0; i < col/2; i++){
                if(grid[row/2][i] != grid[row/2][col - i - 1]) t++;
            }
            for(int i = 0; i < row/2; i++){
                if(grid[i][col/2] != grid[row - i - 1][col/2]) t++;
            }
            for(int i = 0; i < col; i++){
                if(grid[row/2][i] == 1) cnt1++;
            }
            for(int i = 0; i < row; i++){
                if(grid[i][col/2] == 1) cnt1++;
            }
            int mi = cnt1/4*4;
            int ma = (cnt1/4+1)*4;
            if((cnt1 + t) >= ma || (cnt1 - t) <= mi)
                return res + t;
            return res + t + 2;
        }
        return res;
    }
}
