class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])
        i=0
        j=n-1
        c=0
        while(i<j):
            l=0
            k=m-1
            while(l<k):
                o=0
                z=0
                if(grid[i][l]):
                    o+=1
                else:
                    z+=1
                if(grid[i][k]):
                    o+=1
                else:
                    z+=1
                if(grid[j][l]):
                    o+=1
                else:
                    z+=1
                if(grid[j][k]):
                    o+=1
                else:
                    z+=1
                c+=4-max(z,o)
                l+=1
                k-=1
            i+=1
            j-=1
        if(n%2!=0 and m%2!=0):
            if(grid[n//2][m//2]):
                c+=1
        ci=0
        co=0
        if(n%2!=0):
            i=0
            j=m-1
            while(i<j):
                if(grid[n//2][i] and grid[n//2][i]==grid[n//2][j]):
                    ci+=1
                if(grid[n//2][i]!=grid[n//2][j]):
                    co+=1
                i+=1
                j-=1 
        if(m%2!=0):
            i=0
            j=n-1
            while(i<j):
                if(grid[i][m//2] and grid[i][m//2]==grid[j][m//2]):
                    ci+=1
                if(grid[i][m//2]!=grid[j][m//2]):
                    co+=1
                i+=1
                j-=1
        c+=co
        if(ci%2!=0):
            if(co==0):
                c+=2
        return c
