class Solution(object):
    def minFlips(self, grid):
        """
        :type grid: List[List[int]]
        :rtype: int
        """

        col_len = len(grid[0])
        row_len = len(grid)
        swaps = 0

        for row in range(row_len//2):
            for col in range(col_len//2):
                tl = grid[row][col] 
                tr = grid[row_len-row-1][col]
                bl = grid[row][col_len-col-1]
                br = grid[row_len-row-1][col_len-col-1]
                swaps += min(tl+tr+bl+br, 4-(tl+tr+bl+br))

        if col_len%2!=0 and row_len%2!=0 and grid[row_len//2][col_len//2]==1:
            swaps += 1

        sum1 = 0
        oddswaps = 0
        if col_len%2!=0:
            col = col_len//2
            for row in range(row_len//2):
                if grid[row][col] != grid[row_len-1-row][col]:
                    oddswaps += 1
                    sum1+=2
                else:
                    sum1 += grid[row][col] + grid[row_len-1-row][col]

        if row_len%2!=0:
            row = row_len//2
            for col in range(col_len//2):
                if grid[row][col] != grid[row][col_len-1-col]:
                    oddswaps += 1
                    sum1 += 2
                else:
                    sum1 += grid[row][col] + grid[row][col_len-1-col]

        adjustments = 0
        prev_oddswap = oddswaps
        while sum1%4!=0:
            print(sum1)
            if prev_oddswap>0:
                sum1-=2
                prev_oddswap-=1
            else:
                adjustments+=2
                sum1-=2

        return swaps + oddswaps + adjustments
