class Solution {
public:
    int check(vector<vector<int>>& grid, int i, int j, int & count,int n, int m, bool &flip){
        if(n&1 and m&1 and i == n/2 and j == m/2){
            if(grid[i][j] == 1)
                return 1;
            return 0;
        }
        if(n&1 and i == n/2){
            if(grid[i][j] == 1 and grid[i][m-j-1] == 1){
                count++;
                return 0;
            }
            else if(grid[i][j] != grid[i][m-j-1]){
                flip = true;
                return 1;
            }
            return 0;
        }
        if(m&1 and j == m/2){
            if(grid[i][j] == 1 and grid[n-i-1][j] == 1){
                count++;
                return 0;
            }
            else if(grid[i][j] != grid[n-i-1][j]){
                flip = true;
                return 1;
            }
            return 0;
        }
        int ones = 0;
        if(grid[i][j] == 1)
            ones++;
        if(grid[i][m-j-1] == 1)
            ones++;
        if(grid[n-i-1][j] == 1)
            ones++;
        if(grid[n-i-1][m-j-1] == 1)
            ones++;
        if(ones == 2)
            return 2;
        if(ones == 1 or ones == 3)
            return 1;
        return 0;
    }
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int onecount = 0;
        bool flip = false;
        int ans = 0;

        for(int i = 0; i < (n+1)/2; i++){
            for(int j = 0; j < (m+1)/2; j++){
                ans += check(grid, i, j,onecount,n,m,flip);
            }
        }
        if((onecount & 1) and !flip)
            ans += 2;
        return ans;
    }
};
