class Solution {
    public int minFlips(int[][] grid) {
        int row = grid.length;
        int col = grid[0].length;
        int res = 0;

        if (row % 2 == 1 && col % 2 == 1) {
            if (grid[row / 2][col / 2] == 1) {
                res += 1;
            }
        }

        int[] shift = new int[3];

        if (row % 2 == 1) {
            int a = 0, b = col - 1;
            while (a < b) {
                int sm = grid[row / 2][a] + grid[row / 2][b];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (col % 2 == 1) {
            int a = 0, b = row - 1;
            while (a < b) {
                int sm = grid[a][col / 2] + grid[b][col / 2];
                shift[sm] += 1;
                a += 1;
                b -= 1;
            }
        }

        if (shift[2] % 2 == 1) {
            if (shift[1] > 0) {
                res += shift[1];
            } else {
                res += 2;
            }
        } else {
            res += shift[1];
        }

        for (int i = 0; i < row / 2; ++i) {
            for (int j = 0; j < col / 2; ++j) {
                int a1 = grid[i][j];
                int b1 = grid[row - 1 - i][j];
                int c1 = grid[i][col - 1 - j];
                int d1 = grid[row - 1 - i][col - 1 - j];

                int sma = a1 + b1 + c1 + d1;

                if (sma != 0 && sma != 4) {
                    res += Math.min(sma, 4 - sma);
                }
            }
        }

        return res;
    }
}
