class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(),m = g[0].size();
        int c = 0;

        if(n==1 && m==1) {
                if(g[0][0]==0) return 0;
                else return 1;
        } 

        if((n==1 && m==2)) {
            if(g[0][0]==g[0][1]) {
                if(g[0][0]==1) return 2;
                else return 0;
            } else {
                return 1;
            }
        }

        if((m==1 && n==2)) {
            if(g[0][0]==g[1][0]) {
                if(g[0][0]==1) return 2;
                else return 0;
            } else {
                return 1;
            }
        }


        for(int i=0;i<n/2;i++) {
            for(int j=0;j<m/2;j++) {
                int x = 0,y = 0;
                if(g[i][j]==0) x++;
                else y++;
                if(g[i][m-1-j]==0) x++;
                else y++;
                if(g[n-i-1][j]==0) x++;
                else y++;
                if(g[n-i-1][m-j-1]==0) x++;
                else y++;

                c += (4-max(x,y));
            }
        }

        // cout << c << endl;
        if(n%2 && m%2) {
            if(g[n/2][m/2]==1) c++;
            int x = n/2,y = 0;
            int op = 0;
            for(int i=0;i<m/2;i++) {
                if(g[x][m-i-1] != g[x][i]) op++;
                else {
                    if(g[x][i]==1) y += 2;
                }
            }

            c += op;

            x = m/2;
            int op1 = 0;
            // y = 0;
            for(int i=0;i<n/2;i++) {
                if(g[i][x] != g[n-i-1][x]) op1++;
                else {
                    if(g[i][x]==1) y += 2;
                }
            }

            c += op1;
            if(!((op || op1) && y%4)) c += (y%4);


        } else if(n%2) {
            int x = n/2,y = 0;
            int op = 0;
            for(int i=0;i<m/2;i++) {
                if(g[x][m-i-1] != g[x][i]) op++;
                else {
                    if(g[x][i]==1) y += 2;
                }
            }

            c += op;
            if(!(op && y%4)) c += (y%4);
        } else if(m%2){
            int x = m/2,y = 0;
            int op = 0;
            for(int i=0;i<n/2;i++) {
                if(g[i][x] != g[n-i-1][x]) op++;
                else {
                    if(g[i][x]==1) y += 2;
                }
            }

            c += op;
            if(!(op && y%4)) c += (y%4);
        }

        return c;
    }
};
