class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int i=0; i<n/2; i++) {
            for(int j=0; j<m/2; j++) {
                int a = grid[i][j];
                int b = grid[n-1-i][j];
                int c = grid[i][m-1-j];
                int d = grid[n-1-i][m-1-j];

                int sum = a + b + c + d;
                ans += min(sum, 4 - sum);

                if(sum > 4 - sum) {
                    grid[i][j] = 1;
                    grid[n-1-i][j] = 1;
                    grid[i][m-1-j] = 1;
                    grid[n-1-i][m-1-j] = 1;
                } else {
                    grid[i][j] = 0;
                    grid[n-1-i][j] = 0;
                    grid[i][m-1-j] = 0;
                    grid[n-1-i][m-1-j] = 0;
                }
            }
        }

        int one1 = 0, diff1 = 0, one2 = 0, diff2 = 0;
        if(n%2) {
            int row = n/2;
            for(int i=0; i<m/2; i++) {
                if(grid[row][i] != grid[row][m-1-i]) {
                    diff1++;
                }else {

                if(grid[row][i] == 1) {
                    one1++;
                } 
                if(grid[row][m-1-i]) {
                    one1++;
                }
                }
            }
        }

        if(m%2) {
            int col = m/2;
            for(int i=0; i<n/2; i++) {
                if(grid[i][col] != grid[n-1-i][col]) {
                    diff1++;
                } else {
                    if(grid[i][col]) {
                        one1++;
                    }
                    if(grid[n-1-i][col]) {
                        one1++;
                    }

                }
            }
        }

        one1 %= 4;
        if(one1 == 2) {
            if(diff1 >= 1) {
                ans += diff1;
            } else {
                ans += 2;
            }
        } else ans += diff1;

        // if(one2 == 2) {
        //     if(diff2 >= 1) {
        //         ans += diff2;
        //     } else {
        //         ans += 2;
        //     }
        // } else ans += diff2;

        if(n%2 && m%2 && grid[n/2][m/2]) ans++;

        return ans;
    }
};

