class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {
                int n = grid.size(), m = grid[0].size();
        int ans = 0;

        if (n % 2 == 1 && m % 2 == 1)
        {
            if (grid[n / 2][m / 2] == 1)
            {
                ans += 1;
            }
        }

       int c1=0,c2=0;
        if (n&1)
        {
            int l = 0, r = m - 1;
            while (l < r)
            {
                int sm = grid[n / 2][l] + grid[n / 2][r];
             c2+=(sm==2);
                c1+=(sm==1);
                l++;
                r--;
            }
        }

        if (m&1)
        {
            int l = 0, r = n - 1;
            while (l < r)
            {
                int sm = grid[l][m / 2] + grid[r][m / 2];
                  c2+=(sm==2);
                c1+=(sm==1);
                l++;
                r--;
            }
        }

        if (c2 & 1)
        {
            if (c1 > 0)
            {
                ans += c1;
            }
            else
            {
                ans += 2;
            }
        }
        else
        {
            ans += c1;
        }

        for (int i = 0; i < n / 2; ++i)
        {
            for (int j = 0; j < m / 2; ++j)
            {


                int sm =  grid[i][j] + grid[n - 1 - i][j] + grid[i][m - 1 - j]+ grid[n - 1 - i][m - 1 - j];

                if (sm % 4 != 0)
                {
                    ans += min(sm, 4 - sm);
                }
            }
        }

        return ans;





    }
};
