class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        bool vis[n][m];
        memset(vis,0,sizeof(vis));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]){
                    continue;
                }
                if(i==n-1-i || j==m-1-j){
                    continue;
                }
                int sum=grid[i][j];
                    sum+=grid[n-1-i][j];
                    sum+=grid[i][m-1-j];
                    sum+=grid[n-1-i][m-1-j];
                    ans+=min(sum,4-sum);
                    vis[i][j]=1;
                    vis[n-1-i][j]=1;
                    vis[i][m-1-j]=1;
                    vis[n-1-i][m-1-j]=1;

            }
        }
        if(n%2 && m%2){
            int i=n/2;
            int j=m/2;
            int l=0,r=m-1;
            int c=0;
            int ones=0;
            while(l<r){
                c+=(grid[i][l]!=grid[i][r]);
                ones+=(grid[i][l] && grid[i][r]);
                l++,r--;
            }
            l=0,r=n-1;
            while(l<r){
                c+=(grid[l][j]!=grid[r][j]);
                ones+=(grid[l][j] && grid[r][j]);
                l++,r--;
            }
            int u=grid[l][j];
            ans+=c;
            ans+=u;
            if(ones%2 && c==0){
                ans+=2;
            }
        }
        else if(n%2){
            int i=n/2;
            int l=0,r=m-1;
            int c=0;
            int ones=0;
            while(l<r){
                ones+=(grid[i][l] && grid[i][r]);
                c+=(grid[i][l]!=grid[i][r]);
                l++,r--;
            }
            ans+=c;
            if(ones%2 && c == 0){
                ans+=2;
            }
        }
        else if(m%2){
            int i=m/2;
            int l=0,r=n-1;
            int c=0;
            int ones=0;
            while(l<r){
                ones+=(grid[l][i] && grid[r][i]);
                c+=(grid[l][i]!=grid[r][i]);
                l++,r--;
            }
            ans+=c;
            if(ones%2 && c == 0){
                ans+=2;
            }
        }
        return ans;
    }
};
