import (
	"fmt"
	"math"
)

func minFlips(grid [][]int) int {
	rows := len(grid)
	cols := len(grid[0])
	flips := 0

	// Check the center cell for odd rows and columns
	if rows%2 == 1 && cols%2 == 1 {
		if grid[rows/2][cols/2] == 1 {
			flips++
		}
	}

	pairCounts := [3]int{}
	if rows%2 == 1 {
		left, right := 0, cols-1
		for left < right {
			total := grid[rows/2][left] + grid[rows/2][right]
			pairCounts[total]++
			left++
			right--
		}
	}

	if cols%2 == 1 {
		top, bottom := 0, rows-1
		for top < bottom {
			total := grid[top][cols/2] + grid[bottom][cols/2]
			pairCounts[total]++
			top++
			bottom--
		}
	}

	if pairCounts[2]%2 == 1 {
		if pairCounts[1] > 0 {
			flips += pairCounts[1]
		} else {
			flips += 2
		}
	} else {
		flips += pairCounts[1]
	}

	for i := 0; i < rows/2; i++ {
		for j := 0; j < cols/2; j++ {
			topLeft := grid[i][j]
			bottomLeft := grid[rows-1-i][j]
			topRight := grid[i][cols-1-j]
			bottomRight := grid[rows-1-i][cols-1-j]

			total := topLeft + bottomLeft + topRight + bottomRight

			if total != 0 && total != 4 {
				flips += int(math.Min(float64(total), float64(4-total)))
			}
		}
	}

	return flips
}
