class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans =0;
        int x = 0;
        bool z = false;
        vector<vector<int>> vis(n, vector<int>(m));
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j]) continue;
                int count = grid[i][j];
                if(i!=n-i-1 && j!=m-j-1){
                    count+=grid[n-i-1][j] + grid[i][m-j-1]+grid[n-i-1][m-j-1];
                    ans+=min(count, 4-count);
                } else if(i!=n-i-1){
                    count+=grid[n-i-1][j];
                    ans+=min(count, 2-count);
                    if(count==2){
                        x+=2;
                    } else if(count==1) {
                        z = true;
                    }
                } else if(j!=m-j-1){
                    count+=grid[i][m-j-1];
                    ans+=min(count, 2-count);
                    if(count==2){
                        x+=2;
                    } else if(count==1) {
                        z = true;
                    }
                } else {
                    ans+=grid[i][j]==1;
                }
                vis[i][j] = vis[i][m-j-1] = vis[n-i-1][j] = vis[n-i-1][m-j-1] = 1;
            }
        }
        if(!z){
            ans+=(x%4);
        }
        return ans;
    }
};
