class Solution {
public:
    int minFlips(vector<vector<int>>& v) {

        int cnt = 0;

        int n = v.size();

        int m = v[0].size();

        int u = 0, d = n - 1;

        while(u<d){

            int l = 0 , r = m-1;

            while(l<r){

                int one = 0;
                int zero = 0;

                if(v[u][l]) one++;
                else zero++;

                if(v[u][r]) one++;
                else zero++;

                if(v[d][l]) one++;
                else zero++;

                if(v[d][r]) one++;
                else zero++;

                cnt+=min(one,zero);
                l++,r--;
            }
            u++;
            d--;

        }

        int tot = 0;
        int one = 0;

        if(m&1){
            int mid = m /2;

            int l = 0, r = n-1;

            while(l<r){
                if(v[l][mid] == v[r][mid] && v[l][mid]) one+=2;
                else if(v[l][mid] != v[r][mid]) tot++;
                l++;
                r--;
            }
        }

        if(n&1){

            int mid = n/2;

            int l = 0 , r = m-1;

            while(l<r){

                if(v[mid][l] == v[mid][r] && v[mid][l]) one+=2;
                else if(v[mid][l] != v[mid][r]) tot++;

                l++;
                r--;
            }
        }


        if((n&1) && (m&1) && v[n/2][m/2]) cnt++;

        cnt+=tot;

        int rem = one%4;

        if(rem && !tot){
            cnt+=2;
        }

        return cnt;
    }
};
