class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        if(n%2 && m%2 && grid[n/2][m/2]) ans++;
          int p=0;
        int mat=0;
        if(n%2){
          for(int i=0;i<m/2;i++){
            if(grid[n/2][i]!=grid[n/2][m-i-1]){
                p++;
            }
            else if(grid[n/2][i]==1){
                mat++;
            }
          }
        }
        //return p;
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]!=grid[n-1-i][m/2]) p++;
                else if(grid[i][m/2]) mat++;
            }
        }
        //return mat;
        if((mat%2)==0){
            ans+=p;
        }
        else{
            if(p%2 && mat%2){
                ans+=p;
            }
            else{
                if(p>1)
                ans+=(p);
                else{
                    ans+=2;
                }
            }
        }
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int in=0;
                in=(grid[i][j]+grid[n-1-i][j]+grid[n-1-i][m-1-j]+grid[i][m-1-j]);
                ans+=min(in,4-in);

            }
        }
        return ans;
    }
};
