public class Solution
{
    public int MinFlips(int[][] grid)
    {
        int n = grid.Length;
        int m = grid[0].Length;
        int ans = 0;
        for(int i = 0; i < n / 2; i++)
        {
            for(int j = 0; j < m / 2; j++)
            {
                int c0 = 0, c1 = 0;
                if (grid[i][j] == 0) c0++;
                else c1++;
                if (grid[i][m - j - 1] == 0) c0++;
                else c1++;
                if (grid[n - i - 1][j] == 0) c0++;
                else c1++;
                if (grid[n - i - 1][m - j - 1] == 0) c0++;
                else c1++;
                ans += Math.Min(c0, c1);
            }
        }
        if (m % 2 == 1 && n % 2 == 1)
        {
            ans += grid[n / 2][m / 2];
            int c = 0;
            int c1 = 0;
            for(int i = 0; i < n / 2; i++)
            {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) c++;
            }
            for (int j = 0; j < m / 2; j++)
            {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) c++;
            }
            for (int i = 0; i < n; i++) c1 += grid[i][m / 2];
            for (int j = 0; j < m; j++) c1 += grid[n / 2][j];
            c1 -= grid[n / 2][m / 2] * 2;
            ans += c;
            if(c == 0 && c1 % 4 != 0)
            {
                ans += 2;
            }
        }
        else if(m % 2 == 1)
        {
            int c = 0;
            int c1 = 0;
            for(int i = 0; i < n / 2; i++)
            {
                if (grid[i][m / 2] != grid[n - i - 1][m / 2]) c++;
            }
            for (int i = 0; i < n; i++) c1 += grid[i][m / 2];
            ans += c;
            if(c == 0 && c1 % 4 != 0) ans += 2;
        }
        else if(n % 2 == 1)
        {
            int c = 0;
            int c1 = 0;
            for (int j = 0; j < m / 2; j++)
            {
                if (grid[n / 2][j] != grid[n / 2][m - j - 1]) c++;
            }
            for (int j = 0; j < m; j++) c1 += grid[n / 2][j];
            ans += c;
            if (c == 0 && c1 % 4 != 0)
            {
                ans += 2;
            }
        }
        return ans;
    }
}
