class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] != grid[i][m - j - 1]) {
                    ans++;
                    grid[i][j] = 2;
                    grid[i][m - j - 1] = 2;
                }
            }
        }

        for (int j = 0; j < m; j++) {
            for (int i = 0; i < n; i++) {
                if (grid[i][j] == 2 && grid[n - i - 1][j] == 2) continue;
                if (grid[i][j] == 2) grid[i][j] = grid[n - i - 1][j];
                else if (grid[n - i - 1][j] == 2) grid[n - i - 1][j] = grid[i][j];
                else if (grid[i][j] + grid[n - i - 1][j] == 1) {
                    ans++;
                    grid[i][j] = 2;
                    grid[n - i - 1][j] = 2;
                }
            }
        }
        int cnt1 = 0, cnt2 = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 1) cnt1++;
            }
        }

        if (cnt1 & 1) {
            cnt1--;
            ans++;
        }

        if (cnt1 % 4) {
            bool add = true;
            if (m & 1) for (int i = 0; i < n; i++) if (grid[i][m / 2] == 2) add = false;
            if (n & 1) for (int i = 0; i < m; i++) if (grid[n / 2][i] == 2) add = false;
            if (add) ans += 2;
        }

        return ans;
    }
};
