class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n=g.size();
        int m=g[0].size();
        int ans=0;
        vector<vector<int>> vis(n,vector<int>(m,0));
        int x11=0,y01=0,z00=0;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(vis[i][j])continue;
                set<pair<int,int>> bb;
                bb.insert({i,j});
                bb.insert({i,m-j-1});
                bb.insert({n-i-1,j});
                bb.insert({n-i-1,m-j-1});
                int a0=0,a1=0;
                for(auto el:bb){
                    int x=el.first;
                    int y=el.second;
                    vis[x][y]=1;
                    // cout<<"["<<x<<" "<<y<<"]";
                    if(g[x][y]==0)a0++;
                    else a1++;
                }

                if(bb.size()%4==0){
                    ans+=min(a0,a1);
                }else if(bb.size()==1){
                    ans+=a1;
                }else{
                    if(a1==2)x11++;
                    else if(a1==1)y01++;
                }
                // cout<<a0<<" "<<a1<<" "<<ans<<endl;
            }
        }

        if(x11%2==0){
            ans+=y01;
        }else{
            if(y01>0)ans+=y01;
            else ans+=2;
        }

        // cout<<endl;
        return ans;

    }
};
