class Solution {
public:
    int minFlips(vector<vector<int>> a) {
        int n=size(a),m=size(a[0]);
        vector<vi> c0 (n/2+1,vi(m/2+1,0));
        fr(i,0,n/2) {
            fr(j,0,m/2) {
                // same 
                c0[i][j]+=(a[i][j]==0);
                //right up
                c0[i][j]+=(a[i][m-1-j]==0);
                //down left
                c0[i][j]+=(a[n-1-i][j]==0);
                //down right
                c0[i][j]+=(a[n-1-i][m-1-j]==0);
            }
        }
        int cnt=0;
        int ans=0;
        int op=0;
        fr(i,0,n/2){
            fr(j,0,m/2){
                int c1=4-c0[i][j];
                int cn0=c0[i][j];
                ans+=min(c1,cn0);
            }
        }
        // cout<<cnt<<"\n";
        if(m%2) {
            int j=m/2;
            int i=0,k=n-1;
            while(i<k){
                if(a[i][j]!=a[k][j]){
                    op++;
                    ans++;
                }
                else {
                    if(a[i][j]==1) cnt+=2;
                }
                i++,k--;
            }
        }

        if(n%2){
            int i=n/2;
            int j=0,k=m-1;
            while(j<k){
                if(a[i][j]!=a[i][k]) {
                    op++;
                    ans++;
                }
                else {
                    if(a[i][j]==1) cnt+=2;
                }
                j++,k--;
            }
        }

        if(n%2&&m%2&&a[n/2][m/2]==1) ans++;
        if(cnt%4==2) {
            if(op==0) ans+=2; 
        }
        return ans;
    }
};
