class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 0, one = 0;
        map<int, int>mp;

        for(int i = 0; i < n/2; i++){
            for(int j = 0; j < m/2; j++){
                mp[g[i][m-1-j]]+=1;
                mp[g[i][j]]+=1;
                mp[g[n-1-i][m-1-j]]+=1;
                mp[g[n-1-i][j]]+=1;
                int c1 = 0, c0 = 0;
                for(auto [x, y]: mp){
                    if(x == 0){
                        c0 = y;
                    } else{
                        c1 = y;
                    }
                }
                ans += 4 - max(c1, c0);
                mp.clear();
            }
        }
        cout<<ans<<'\n';
        int fix = 0;
        if(n&1){
            for(int i = 0; i < m/2; i++){
                if(g[n/2][i] != g[n/2][m-1-i]){
                    ans++;
                    one++;
                } else if(g[n/2][i] == 1){
                    fix++;
                }
            }
        }
        if(m&1){
            for(int i = 0; i < n/2; i++){
                if(g[i][m/2] != g[n-1-i][m/2]){
                    one++;
                    ans++;
                } else if(g[i][m/2] == 1){
                    fix++;
                }
            }
            if(n&1){
                if(g[n/2][m/2] == 1)ans++;
            }
        }
        cout<<fix<<" "<<one<<'\n';
        if(fix&1){
            if(one > 0){}
            else ans += 2;
        }
        return ans;

    }
};
