class Solution {
public:
    int minFlips(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size(), res = 0;
        if (n % 2 && m % 2 && g[n / 2][m / 2]) res += 1;
        vector<int> cnt(3, 0);
        if (n % 2) {
            int x = 0, y = m - 1;
            while (x < y) {
                int c = g[n / 2][x] + g[n / 2][y];
                cnt[c] += 1;
                x += 1;
                y -= 1;
            }
        }
        if (m % 2) {
            int x = 0, y = n - 1;
            while (x < y) {
                int c = g[x][m / 2] + g[y][m / 2];
                cnt[c] += 1;
                x += 1;
                y -= 1;
            }
        }
        if (cnt[2] % 2) res += (cnt[1] > 0) ? cnt[1] : 2;
        else res += cnt[1];
        for (int i = 0; i < n / 2; ++i) {
            for (int j = 0; j < m / 2; ++j) {
                int c1 = g[i][j], c2 = g[n - 1 - i][j];
                int c3 = g[i][m - 1 - j], c4 = g[n - 1 - i][m - 1 - j];
                int t = c1 + c2 + c3 + c4;
                if (t && t != 4) res += min(t, 4 - t);
            }
        }
        return res;
    }
};
