class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = (int)grid.size(), m = (int)grid[0].size();
        auto& a = grid;
        for (int i = 0; i < n / 2; i++) {
            for (int j = 0; j < m / 2; j++) {
                vector<int> h = {a[i][j], a[i][m - 1 - j], a[n - 1 - i][j], a[n - 1 - i][m - 1 - j]};

                vector<int> f(2);
                for (auto& u : h)
                    f[u]++;

                int min_fq = min(f[0], f[1]);
                ans += min_fq;
            }
        }

        vector freq(3, vector<int>(3, 0));
        if (n % 2) {
            int nm = (n + 1) / 2;
            for (int i = 0; i < m / 2; i++) {
                vector<int> f(2);
                f[a[nm - 1][i]]++;
                f[a[nm - 1][m - 1 - i]]++;
                freq[f[0]][f[1]]++;
            }
        }
        if (m % 2) {
            int mm = (m + 1) / 2;
            for (int i = 0; i < n / 2; i++) {
                vector<int> f(2);
                f[a[i][mm - 1]]++;
                f[a[n - 1 - i][mm - 1]]++;
                freq[f[0]][f[1]]++;
            }
        }

        if (n % 2 == 1 and m % 2 == 1) {
            ans += a[(n + 1) / 2 - 1][(m + 1) / 2 -1];
        }

        ans += freq[1][1];
        if (freq[1][1] == 0 && (freq[0][2] % 2) == 1) 
            ans += 2;
        return ans;
    }
};
