class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size(),m = grid[0].size(),one = 0,ans = 0;
        int left = 0,right = m-1;
        while(left < right) {
            int i = 0,j = n-1;


            while(i < j) {
                int a = 0,b = 0;
                if(grid[i][left] == 0) b++;
                else a++;

                if(grid[i][right] == 0) b++;
                else a++;

                if(grid[j][left] == 0) b++;
                else a++;

                if(grid[j][right] == 0) b++;
                else a++;

                ans += min(a,b);
                i++;
                j--;
            }
            left++;
            right--;
        }
        int e = 0;
        if(n%2) {
            int mid = n/2;
            int i = 0, j = m-1;
            while(i < j) {
                if(grid[mid][i] == grid[mid][j] and grid[mid][j]) one += 2;
                if(grid[mid][i] != grid[mid][j]) {
                    ans++;
                    e++;
                }
                i++;
                j--;
            }
        }

        if(m%2) {
            int mid = m/2;
            int i = 0, j = n-1;
            while(i < j) {
                if(grid[i][mid] == grid[j][mid] and grid[j][mid]) one += 2;
                if(grid[i][mid] != grid[j][mid]) {
                    ans++;
                    e++;
                }
                i++;
                j--;
            }
        }
        //cout << n << ' ' << m << ' ' << one << ' ' << e << ans << endl;
        if(n%2 and m%2 and grid[n/2][m/2]) ans++;
        if(one%4) {
            if(e == 0) ans += 2;
        }
        return ans;
    }
};
