class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n, m = len(grid), len(grid[0])
        ans = 0

        for i in range(n // 2):
            for j in range(m // 2):
                suma = grid[i][j] + grid[i][-1-j] + grid[-1-i][j] + grid[-1-i][-1-j]

                if (suma == 0 or suma == 4):
                    continue

                grid[i][j], grid[i][-1-j], grid[-1-i][j], grid[-1-i][-1-j] = 0,0,0,0

                ans += min(suma, 4 - suma)

        mid = 0

        if (m % 2 == 1):
            for i in range(n // 2):
                if (grid[i][m // 2] != grid[-1-i][m // 2]):
                    mid += 1
                    grid[i][m // 2], grid[-1-i][m // 2] = 1, 1

        if (n % 2 == 1):
            for j in range(m // 2):
                if (grid[n // 2][j] != grid[n // 2][-1-j]):
                    mid += 1
                    grid[n // 2][j], grid[n // 2][-1-j] = 1, 1

        ones = sum([sum(row) for row in grid]) % 4
        zeros = n + m - 1 - ones

        if (ones % 2 == 1):
            ans += 1
            ones -= 1

        if (ones == 2 and mid == 0):
            ans += 2

        return ans + mid
